using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using System.Threading.Tasks;
using Microsoft.Playwright;

namespace ExamTopicCrawler.Services
{
    public class PlaywrightService
    {
        public IPlaywright Playwright { get; private set; }
        public IBrowser Browser { get; private set; }
        public IPage Page { get; private set; }

        public async Task InitAsync(bool headless = true)
        {
            Playwright = await Microsoft.Playwright.Playwright.CreateAsync();
            Browser = await Playwright.Chromium.LaunchAsync(new()
            {
                Headless = headless,
                Args = new[] { "--no-sandbox" }
            });

            Page = await Browser.NewPageAsync();
        }

        public async Task LoginAsync(string startUrl, string email, string password)
        {
            // Navigate to the start page
            Console.WriteLine($"Navigating to: {startUrl}");
            await Page.GotoAsync(startUrl);
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                