                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            await Page.WaitForLoadStateAsync(LoadState.NetworkIdle);

            Console.WriteLine("Checking for login modal...");

            // Check if login modal is already visible
            var isModalVisible = await Page.IsVisibleAsync("#login-modal.show").ConfigureAwait(false);
            
            if (!isModalVisible)
            {
                Console.WriteLine("Modal not visible, attempting to trigger it...");
                await TriggerLoginModalAsync();
            }

            // Wait for and handle the login modal
            try
            {
                // Wait for login modal to appear
                Console.WriteLine("Waiting for login modal...");
                var loginModal = await Page.WaitForSelectorAsync("#login-modal", new() { Timeout = 10000 });
                
                if (loginModal != null)
                {
                    Console.WriteLine("Login modal detected, filling credentials...");
                    
                    // Wait a bit for modal animation
                    await Task.Delay(500);
                    
                    // Fill email/username
                    await Page.FillAsync("input[name='email']", email);
                    Console.WriteLine($"Filled email: {email}");
                    
                    await Task.Delay(300);
                    
                    // Fill password
                    await Page.FillAsync("input[name='password']", password);
                    Console.WriteLine("Filled password");
                    
                    await Task.Delay(300);
                    
                    // Click login button
                    await Page.ClickAsync("button.login-button[type='submit']");
                    Console.WriteLine("Clicked login button");
                    
                    // Wait for navigation or modal to close after login
                    await Task.Delay(3000); // Give time for login to process
                    
                    // Check if login was successful (modal should be hidden)
                    var modalStillVisible = await Page.IsVisibleAsync("#login-modal.show");
                    if (!modalStillVisible)
                    {
                        Console.WriteLine("✓ Login successful!");
                    }
                    else
                    {
                        Console.WriteLine("⚠ Login modal still visible - check credentials");
                    }
                }
            }
            catch (Exception ex)
            {
                Console.WriteLine($"❌ Login error: {ex.Message}");
                throw;
            }
        }

        public async Task TriggerLoginModalAsync()
        {
            // If login modal doesn't auto-show, this can trigger it
            try
            {
                // Look for login trigger button/link
                var loginTrigger = await Page.QuerySelectorAsync("a[href='/login/'][data-toggle='modal'], a[data-target='#login-modal']");
                if (loginTrigger != null)
                {
                    Console.WriteLine("Clicking login button to trigger modal...");
                    await loginTrigger.ClickAsync();
                    await Page.WaitForSelectorAsync("#login-modal.show", new() { Timeout = 3000 });
                    Console.WriteLine("Login modal opened successfully");
                }
                else
                {
                    Console.WriteLine("Login button not found, modal might already be visible");
                }
            }
            catch (Exception ex)
            {
                Console.WriteLine($"Could not trigger login modal: {ex.Message}");
            }
        }
    }
}
